changeSet.sql:
UPDATE PTG_VENTIL_HSUP a SET M_NORMALES = 
		( select b.m_complementaires from PTG_VENTIL_HSUP b WHERE b.m_complementaires <> 0 and a.id_ventil_hsup=b.id_ventil_hsup) 
		WHERE a.M_NORMALES = 0 and a.m_complementaires <> 0 ;

UPDATE PTG_VENTIL_HSUP a SET m_normales_r = 
		( select b.m_complementaires_r from PTG_VENTIL_HSUP b WHERE b.m_complementaires_r <> 0 and a.id_ventil_hsup=b.id_ventil_hsup) 
		WHERE a.m_normales_r = 0 and a.m_complementaires_r <> 0 ;
